export default class MapEventManager {

  constructor() {
    this.maps = {};
    // this.maps = {
    //   '[map1._leaflet_id]': {
    //     map: 'MapObject',
    //     handlers: {
    //       'zoomend': function() {},
    //       'layeradd': function() {},
    //       'layerremove': function() {}
    //     },
    //     callbacks: {
    //       '[LeafletLayer1._leaflet_id]': ['callback1'],
    //       '[LeafletLayer2._leaflet_id]': ['callback2', 'callback2', ... ]
    //     }
    //   }
    // };
  }

  bindLayerToMapChanges(map, layer, callback) {
    console.log('MapEventManager binding layer', layer._leaflet_id, 'to map', map._leaflet_id);
    this._addCallback(map, layer, callback);
  }

  // expects to be bound with .bind(this, map)
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Function/bind
  _zoomendHandler(map, e) {
    // a zoom change needs to call all the callbacks
    // because only the model can compute whether or not it is visible

    console.log('MapEventManager detected zoom level changed to', e.target.getZoom());
    let id;
    let callbacks;
    let callbackList = this._findMap(map).callbacks
    for (id in callbackList) {
      callbacks = callbackList[id];
      if (callbacks) {
        console.log('MapEventManager zoom found callbacks', callbacks);
        callbacks.forEach((callback) => {
          callback(e)
        })
      }
    }
  }

  // expects to be bound with .bind(...)
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Function/bind
  _layeraddHandler(map, e) {
    // a layer add/remove change needs only to call one handler:
    // the handler corresponding to the layer that was added/removed

    console.log('MapEventManager detected layer added/removed', e);
    let callbacks = this._findCallbacks(map, e.layer)
    if (callbacks) {
      console.log('MapEventManager calling add/remove callbacks', callbacks);
      callbacks.forEach((callback) => {
        callback(e)
      })
    }
  }

  // expects to be bound with .bind(...)
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Function/bind
  _layerremoveHandler(map, e) {
    this._layeraddHandler(map, e);
  }

  // adds a new map if the specified one does not exist
  _findMap(map) {
    let entry = this.maps[map._leaflet_id];
    if (!entry) {
      entry = this._addNewMap(map);
    }
    return entry;
  }

  _addNewMap(map) {
    let entry = this.maps[map._leaflet_id] = {
      map,
      handlers: {
        'zoomend': this._zoomendHandler.bind(this, map),
        'layeradd': this._layeraddHandler.bind(this, map),
        'layerremove': this._layerremoveHandler.bind(this, map)
      },
      callbacks: {}
    };
    this._addMapHandlers(map);
    return entry;
  }

  _findCallbacks(map, layer) {
    let mapEntry = this._findMap(map);
    if (!mapEntry.callbacks[layer._leaflet_id]) {
      mapEntry.callbacks[layer._leaflet_id] = [];
    }
    console.log('MapEventManager found callbacks for', layer._leaflet_id, mapEntry.callbacks[layer._leaflet_id]);
    return mapEntry.callbacks[layer._leaflet_id];
  }

  _addCallback(map, layer, callback) {
    let callbacks = this._findCallbacks(map, layer);
    callbacks.push(callback);
    console.log('MapEventManager adding callback to layer (+ result)', layer._leaflet_id, callbacks);
  }

  _addMapHandlers(map) {
    console.log('MapEventManager adding handlers to map');
    // http://leafletjs.com/reference-1.2.0.html#evented-on
    map.on(this._findMap(map).handlers);
  }

  _removeMapHandlers(map) {
    console.log('MapEventManager removing handlers from map');
    // http://leafletjs.com/reference-1.2.0.html#evented-off
    map.off(this._findMap(map).handlers);
  }

}

import { defaultsDeep as defaults } from 'lodash';
// superagent mimics the node 'request' library on the client side as an xhr library
import superagent from 'superagent';
import Promise from 'bluebird';
import superagentPromise from 'superagent-promise';
const request = superagentPromise(superagent, Promise);

import NestedDynamicMapLayer from './Leaflet.TOC.NestedDynamicMapLayer';
import NestedGroupLayer from './Leaflet.TOC.NestedGroupLayer';
import * as NestedLayerTreeHelper from './Leaflet.TOC.NestedLayerTreeHelper';
import MapSymbol from './Leaflet.TOC.MapSymbol';
import { DynamicMapLayer } from 'esri-leaflet';

export default class MapServerParser {

  static _defaults = {
    parsingOptions: {
      scale: true,
      defaultVisibility: true,
      symbology: false
    },
    layerProps: {},
    dynamicMapLayerOptions: {}
  };

  static APIEndpoints = {
    layers: '/layers',
    legend: '/legend'
  };

  static QueryParameters = {
    f: 'json'
  };

  static Headers = {
    Accept: 'application/json'
  };

  constructor(url, map, options) {
    // ensure required parameters are present
    if (typeof url == 'undefined') {
      throw new Error('Missing URL when creating MapServerParser');
    }
    if (typeof map == 'undefined') {
      throw new Error('Missing Leaflet map object when creating MapServerParser');
    }

    // save url
    this._url = url;

    // save map
    this._map = map;

    // https://lodash.com/docs/4.17.4#defaultsDeep
    this._options = defaults({}, options, this.defaults);

    // create a new DynamicMapLayer to use in all of our NestedDynamicMapLayers
    let layerOptions = defaults({ url }, this.options.dynamicMapLayerOptions)
    this._layer = new DynamicMapLayer(layerOptions);
  }

  /**
   * MapServer URL
   * @type {string}
   */
  get url() {
    return this._url;
  }

  /**
   * Leaflet Map object
   * @type {L.Map}
   */
  get map() {
    return this._map;
  }

  /**
   * Configuration options
   * @type {object}
   */
  get options() {
    return this._options;
  }

  /**
   * Default configuration options
   * @type {object}
   */
  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  /**
   * Esri-Leaflet DynamicMapLayer object
   * @type {DynamicMapLayer}
   */
  get layer() {
    return this._layer;
  }

  parse() {
    // we have to wait for both xhr promises to resolve before we can begin processing
    // note that Promise.join returns a promise that resolves to a LayerHierarchy
    return Promise.join(this._queryLayers(), this._queryLegend(), (layerResponse, legendResponse) => {
      // ensure we hit HTTP 2xx statuses
      if (!layerResponse.ok || !legendResponse.ok) {
        throw new Error('Bad request: HTTP status was not 2xx');
      }

      // parse the layer data into JSON
      let layers = this._parseBodyAsJSON(layerResponse);

      // parse the legend data into JSON
      let legend = this._parseBodyAsJSON(legendResponse);

      // restrict ourselves to the 'layers' node of the response bodies
      layers = layers.layers;
      legend = legend.layers;

      let tree = [];

      layers.forEach((node) => {
        let layer = this._parseNode(node);

        if (this.options.parsingOptions.defaultVisibility) {
          layer.selected = node.defaultVisibility;
        }

        if (this.options.parsingOptions.symbology) {

          // find the entry in the legend corresponding to the current layer
          let legendEntry = legend.find((legendNode) => {
            return legendNode.layerId == layer.layerID;
          })

          // confirm that there is symbology for the layer
          if (legendEntry && legendEntry.legend && legendEntry.legend.length > 0) {

            // add each entry from the legend node in the entry as a MapSymbol
            legendEntry.legend.forEach((entry) => {
              layer.symbology.addSymbol(new MapSymbol(entry));
            })

          }
        }

        if (this.options.parsingOptions.scale) {
          // convert the 'scale' data returned by ArcGIS to 'zoom' data used by Leaflet
          // if ArcGIS returns '0', set to Inifinity or -Infinity where appropriate
          // http://leafletjs.com/reference-1.2.0.html#crs-zoom
          // https://github.com/Leaflet/Leaflet/blob/master/src/map/Map.js#L39
          const leafletMostZoomedInLevel = 19;
          const leafletLeastZoomedInLevel = 1;
          const max = (node.maxScale !== 0 ? leafletLeastZoomedInLevel + this.map.options.crs.zoom(node.maxScale) : Number.POSITIVE_INFINITY);
          const min = (node.minScale !== 0 ? leafletMostZoomedInLevel - this.map.options.crs.zoom(node.minScale) : Number.NEGATIVE_INFINITY);
          console.log('MapServerParser min', min, 'max', max);
          // console.log('nodemax', node.maxScale, 'min', min, 'nodemin', node.minScale, 'max', max);
          layer.minZoom = min;
          layer.maxZoom = max;
        }

        // add the layer into the proper subtree
        if (node.parentLayer !== null) {
          let parentID = node.parentLayer.id;
          // this assumes that the parent has already been parsed
          let parent = NestedLayerTreeHelper.getLayerByLayerID(tree, parentID);
          parent.addChild(layer);
        } else {
          tree.push(layer);
        }
      })

      NestedLayerTreeHelper.validateTreeEnabledState(tree);

      return tree;
    });
  }

  _queryLayers() {
    // assemble layerdata url
    const layerdataURL = this.url + this.constructor.APIEndpoints.layers;

    // fetch layerdata as JSON
    return request
      .get(layerdataURL)
      .set(this.constructor.Headers)
      .query(this.constructor.QueryParameters)
      .end();
  }

  _queryLegend() {
    // assemble legend url
    const legendURL = this.url + this.constructor.APIEndpoints.legend;

    // fetch legend as JSON
    return request
      .get(legendURL)
      .set(this.constructor.Headers)
      .query(this.constructor.QueryParameters)
      .end();
  }

  _parseBodyAsJSON(response) {
    // parse the data into JSON
    let body = response.body;
    // ArcGIS does not properly set its Content-Type header
    // so force JSON parsing if superagent did not parse automatically
    if (response.type !== this.constructor.Headers.Accept) {
      body = JSON.parse(response.text);
    }

    return body;
  }

  _parseNode(node) {
    let layerType;

    switch (node.type) {
      case 'Group Layer':
        layerType = NestedGroupLayer;
        break;
      case 'Feature Layer':
        layerType = NestedDynamicMapLayer
        break;
      default:
        throw 'Invalid layer type: ' + node.type;
    }

    return new layerType(
      node.id,
      node.name,
      this.layer,
      this.map,
      this.options.layerProps);
  }

}

import { defaultsDeep as defaults } from 'lodash';

export default class MapSymbol {

  static _defaults = {
    label: '',
    height: 20,
    width: 20,
    contentType: 'image/png',
    imageData: '',
    relativeURL: ''
  }

  constructor(props) {
    // merge props with defaults
    this._props = {};
    // https://lodash.com/docs/4.17.4#defaultsDeep
    defaults(this._props, props, this.defaults);
  }

  get label() {
    return this._props.label;
  }

  get height() {
    return this._props.height;
  }

  get width() {
    return this._props.width;
  }

  get contentType() {
    return this._props.contentType;
  }

  get imageData() {
    return this._props.imageData;
  }

  get relativeURL() {
    return this._props.relativeURL;
  }

  get base64URI() {
    return 'data:' + this.contentType + ';base64,' + this.imageData;
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }
}

export default class MapSymbology {

  constructor(symbols = []) {
    this._symbols = symbols;
  }

  get symbols() {
    return this._symbols;
  }
  addSymbol(symbol) {
    this._symbols.push(symbol);
  }

  get isSingle() {
    return this.symbols.length == 1;
  }

}

import NestedLayer from './Leaflet.TOC.NestedLayer';

export default class NestedDynamicMapLayer extends NestedLayer {
  get isAttached() {
    let layers = this.layer.getLayers() || [];
    return (this.map.hasLayer(this.layer) && layers.includes(this.layerID));
  }

  _attach() {
    if (!this.isAttached) {
      // push the layerID into the list of layers to render
      // http://esri.github.io/esri-leaflet/api-reference/layers/dynamic-map-layer.html#methods

      let layers = this.layer.getLayers();
      // if the DynamicMapLayer was instantiated without providing a list of layer IDs as an option,
      // then .getLayers will return 'false' (as of esri-leaflet@2.0.8)
      // in JavaScript, 'typeof []'' will return 'object', so use Array.isArray to verify that .getLayers
      // returned an actual array
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/typeof
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/isArray
      if (!Array.isArray(layers)) {
        // .getLayers returned false (see above), so set the empty array ourselves
        layers = [];
      }
      // setLayers allows you to introduce duplicate IDs
      // this check prevents that from happening
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/includes
      if (!layers.includes(this.layerID)) {
        // Array.push returns the new length of the array, not the array itself
        // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/push
        layers.push(this.layerID);
        this.layer.setLayers(layers);
      }


      // ensure the Leaflet layer is actually on the map object
      if (!this.map.hasLayer(this.layer)) {
        this.map.addLayer(this.layer);
      }
    }
  }

  _detach() {
    if (this.isAttached) {
      // remove the layerID from the list of layers to render
      // http://esri.github.io/esri-leaflet/api-reference/layers/dynamic-map-layer.html#methods
      let layers = this.layer.getLayers().filter((id) => {
        return id !== this.layerID;
      });
      this.layer.setLayers(layers);

      // if there are no more layers left, remove the layer from the map object
      if (layers.length === 0 && this.map.hasLayer(this.layer)) {
        this.map.removeLayer(this.layer);
      }
    }
  }
}

import NestedLayer from './Leaflet.TOC.NestedLayer';

export default class NestedGroupLayer extends NestedLayer {
  _attach() {
    // intentional noop
  }

  _detach() {
    // intentional noop
  }
}

import { defaultsDeep as defaults } from 'lodash';

import * as NestedLayerTreeHelper from './Leaflet.TOC.NestedLayerTreeHelper';
import MapSymbology from './Leaflet.TOC.MapSymbology';

let lastID = 0;

/**
 * Generate a ID number guaranteed to be unique.
 * IDs are composed of a pseudorandom 4-digit number suffixed by an incrementing counter.
 * @return {Number} Unique ID number
 */
export function generateID() {
  // generates a 4 digit random number
  // lastID is appended to the end of the number
  // lastID is incremented each
  return Number.parseInt(Math.floor(1000 + Math.random() * 9000).toString()
                         + (lastID++).toString());
}

/**
 * Wraps a Leaflet Layer to allow that layer to be the "parent" of other layers by having
 * "child" layers
 * @see http://leafletjs.com/reference-1.1.0.html#layer
 */
export default class NestedLayer {

  static _defaults = {
    parent: null,
    children: [],
    enabled: true,
    selected: false,
    minZoom: Number.NEGATIVE_INFINITY,
    maxZoom: Number.POSITIVE_INFINITY,
    rules: {
      enableTriggersAttach: true,
      alwaysDeselectedWhenDisabled: true,
      disableDescendentsWhenDeselected: true
    }
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  /**
   *
   * @param {object} props The layer data
   * @param {number} props.id The layer's unique ID number
   * @param {string} props.name The layer's unique name
   * @param {L.Layer} props.layer A {@link
   * http://leafletjs.com/reference-1.1.0.html#layer Leaflet layer}
   * @param {L.Map} props.map {@link
   * http://leafletjs.com/reference-1.1.0.html#map Leaflet map} to attach to
   * @param {boolean} [props.enabled=true] Initial enabled state of the layer
   * @param {boolean} [props.selected=false] Initial selected state of the layer
   * @param {string} [props.swatch=''] Base64 encoded swatch PNG
   * @param {NestedLayer[]} [props.children=[]] Child layers
   * @param {number} [props.minZoom] Minimum zoom level that the layer should be visible
   * @param {number} [props.maxZoom] Maximum zoom level that the layer should be visible
   */

  /**
   * Creates a new NestedLayer.
   * @param  {number} layerID ID of the layer on the origin.
   * @param  {string} name    Name of the layer on the origin; display-friendly
   * @param  {L.Layer} layer   Leaflet layer
   * @param  {L.Map} map     Leaflet map
   * @param  {object} props   Optional properties
   */
  constructor(layerID, name, layer, map, props) {

    // ensure all required props are present
    if (typeof layerID == 'undefined') {
      throw new Error('Missing layer ID when creating NestedLayer');
    }
    if (typeof name == 'undefined') {
      throw new Error('Missing name when creating NestedLayer');
    }
    if (typeof layer == 'undefined') {
      throw new Error('Missing layer object when creating NestedLayer');
    }
    if (typeof map == 'undefined') {
      throw new Error('Missing map object when creating NestedLayer');
    }

    // set the required props
    this._props = {
      layerID,
      name,
      layer,
      map
    };

    // this prop isn't set on defaults
    // if it is, then all instances that use the default will share the same instance
    // (NOT what you want)
    this._props.symbology = new MapSymbology()

    // merge optional props with defaults
    // the "defaults" also contains the initial state
    defaults(this._props, props, this.defaults);
    this._props.id = generateID();

    // if this layer is starting off selected, attach to the map
    // calling this.select() ensures that we follow any other attachment rules
    // built into the 'selected' setter
    if (this._props.selected) {
      this.select();
    }

    // if the zoom properties are on the leaflet layer object, copy them up to the
    // NestedLayer object
    // if (this.layer.minZoom !== undefined && this.layer.maxZoom !== undefined) {
    //   this._props.minZoom = this.layer.minZoom;
    //   this._props.maxZoom = this.layer.maxZoom;
    // }

    // if this layer has zoom data, we need to handle the case where the user zooms to a level where
    // our layer should be disabled according to the minZoom/maxZoom contained in the layer object
    // if (this.minZoom !== undefined && this.maxZoom !== undefined) {
    //   this.map.on('zoomend', this._handleMapZoom);
    // }
  }

  /**
   * Layer ID as specified by the server
   * @type {number}
   */
  get layerID() {
    return this._props.layerID;
  }

  /**
   * Layer name
   * @type {string}
   */
  get name() {
    return this._props.name;
  }

  /**
   * Underlying Leaflet layer
   * @type {L.Layer}
   */
  get layer() {
    return this._props.layer;
  }

  /**
   * Leaflet Map to attach to
   * @type {L.Map}
   */
  get map() {
    return this._props.map;
  }

  /**
   * Child layers
   * @type {NestedLayer[]}
   */
  get children() {
    return this._props.children;
  }

  /**
   * Parent layer
   * @type {NestedLayer}
   */
  get parent() {
    return this._props.parent;
  }
  set parent(val) {
    this._props.parent = val;
  }

  /**
   * Map symbology
   * @type {MapSymbology}
   */
  get symbology() {
    return this._props.symbology;
  }

  /**
   * Minimum zoom level for this layer to be visible
   * @type {number}
   */
  get minZoom() {
    return this._props.minZoom;
  }
  set minZoom(val) {
    this._props.minZoom = val;
  }

  /**
   * Maximum zoom level for this layer to be visible
   * @type {number}
   */
  get maxZoom() {
    return this._props.maxZoom;
  }
  set maxZoom(val) {
    this._props.maxZoom = val;
  }

  get rules() {
    return this._props.rules;
  }

  /**
   * Unique ID generated for each NestedLayer
   * @type {number}
   */
  get id() {
    return this._props.id;
  }

  // enabled = user may freely toggle this layer on and off
  // disabled = user may not toggle the layer
  // disabling always deselects the layer, but the selected state is persisted, so that if
  // the layer is re-enabled, the selected state is what it was prior to disabling
  // i.e. if disabled, always deselected
  // this logic is handled in the .selected getter

  /**
   * Whether the user may freely toggle this layer on and off
   * @type {boolean}
   */
  get enabled() {
    return this._props.enabled;
  }
  set enabled(val) {
    this._props.enabled = val;

    // if disabling, detach from map
    if (!val) {
      this._detach();

    // if enabling, and marked selected (i.e. "re-enabling"), attach to map
    } else if (this.rules.enableTriggersAttach && this._props.selected) {
      this._attach();
    }
  }
  /**
   * The inverse of #enabled
   * @type {boolean}
   */
  get disabled() {
    return !this.enabled;
  }

  /** Enable the layer */
  enable() {
    this.enabled = true;
  }
  /** Disable the layer */
  disable() {
    this.enabled = false;
  }
  /** Toggle the layer's enabled state */
  toggleEnabled() {
    this.enabled = !this.enabled;
  }

  // selected = layer present on the map
  // deselected = layer not present on the map
  get selected() {
    if (this.rules.alwaysDeselectedWhenDisabled && this.disabled) {
      return false;
    } else {
      return this._props.selected;
    }
  }
  set selected(val) {
    this._props.selected = val;

    // attach/detach from map when needed
    // disable children from selection when unselected
    if (this.selected) {
      this._attach();
      // this.enableChildren();
    } else {
      this._detach();
      // this.disableChildren();
    }

    // if configured to, disable all descendents if deselected
    if (this.rules.disableDescendentsWhenDeselected) {
      NestedLayerTreeHelper.applyStateChangeToTree(this.children, { enabled: this.selected });
    }
  }
  get deselected() {
    return !this.selected;
  }
  select() {
    this.selected = true;
  }
  deselect() {
    this.selected = false;
  }
  toggleSelected() {
    this.selected = !this.selected;
  }

  /**
   * Checks if the layer could be visible on the map
   * @type {boolean}
   */
  get visible() {
    return this.selected && this._withinZoomRange();
  }

  /**
   * Checks if is bound to the Leaflet map
   * @type {boolean}
   */
  get isAttached() {
    return this.map.hasLayer(this.layer);
  }

  // true if the layer has children
  get hasChildren() {
    return this.children.length > 0;
  }

  // add a child NestedLayer object
  addChild(child) {
    if (!(child instanceof NestedLayer)) {
      throw new TypeError('child is not a NestedLayer');
    }
    child.parent = this;
    this._props.children.push(child);
  }

  // returns true if the layer could be visible at the current zoom level at the map
  _withinZoomRange() {
    // if no range specifies, assume that it is always valid
    if (!this.minZoom || !this.maxZoom) {
      return true;
    }
    // check if the layer is visible at the current zoom level
    const currentZoomLevel = map.getZoom();
    const aboveMinZoom = currentZoomLevel >= this.minZoom;
    const belowMaxZoom = currentZoomLevel <= this.maxZoom
    const withinZoomRange = aboveMinZoom && belowMaxZoom;
    // console.log('within zoom range?', withinZoomRange,
                // 'min:', this.minZoom, '<=', currentZoomLevel, aboveMinZoom,
                // 'max:', this.maxZoom, '>=', currentZoomLevel, belowMaxZoom);
    return (currentZoomLevel >= this.minZoom && currentZoomLevel <= this.maxZoom);
  }

  // display on map
  _attach() {
    if (!this.isAttached && !this.map.hasLayer(this.layer)) {
      this.map.addLayer(this.layer);
      // this.layer.addTo(this.map);
    }
  }

  // remove from map
  _detach() {
    if (this.isAttached && this.map.hasLayer(this.layer)) {
      this.map.removeLayer(this.layer);
      // this.layer.removeFrom(this.map);
    }
  }

}

// import { assign } from 'lodash';

// recursively traverses down the tree looking for the first matching NestedLayer
// "tree" here is expected to be an array of NestedLayers
export function getLayerByUniqueProp(tree, key, val) {
  for (let i = 0; i < tree.length; i++) {

    if (tree[i][key] === val) {
      // base case A: we found a matching element at the root of the tree
      return tree[i];

    } else if (tree[i].hasChildren) {

      // recursive step: search the element's tree
      let result = getLayerByUniqueProp(tree[i].children, key, val);
      if (result !== null) {
        // base case B: element found within a subtree
        return result;
      }

    }

  }

  // base case C: not found
  return null;
}

export function getLayerByID(tree, id) {
  return getLayerByUniqueProp(tree, 'id', id);
}

export function getLayerByLayerID(tree, id) {
  return getLayerByUniqueProp(tree, 'layerID', id);
}

export function applyStateChangeToLayer(layer, change) {
  // copies each key/value in "change" onto "layer"
  // triggers the getters/setters in NestedLayer
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Object/assign
  Object.assign(layer, change);
}

/**
 * Applies a state change (or changes) to a tree or substree
 * @param  {NestedLayer[]} tree   Tree or subtree of NestedLayers
 * @param  {Object} change A key/value pair of state changes to apply to each layer in the tree
 */
export function applyStateChangeToTree(tree, change) {
  for (let i = 0; i < tree.length; i++) {
    applyStateChangeToLayer(tree[i], change);

    if (tree[i].hasChildren) {
      applyStateChangeToTree(tree[i].children, change);
    }
  }
}

export function validateTreeEnabledState(tree) {
  console.log('validating enabled state of tree', tree);
  for (let i = 0; i < tree.length; i++) {
    console.log('is tree[i] selected?', tree[i].selected);
    if (tree[i].selected && tree[i].children.length > 0) {
      applyStateChangeToTree(tree[i].children, { enabled: true });
      validateTreeEnabledState(tree[i].children);
    }
  }
}

/**
 * Modifies (if necessary) the enabled state of layers in the tree according to the following rules:
 * 1. All the descendents of an enabled layer are also enabled.
 * 2. All the descendents of a disabled layer are also disabled.
 * @param  {[type]} tree [description]
 * @return {[type]}      [description]
 */
