        let p = new MapServerParser();
      }).to.throw();
      expect(() => {
        let p = new MapServerParser(undefined, map, options);
      }).to.throw('URL');
      expect(() => {
        let p = new MapServerParser(url, undefined, options);
      }).to.throw('map');
    });

