import NestedLayer, { generateID } from './../src/Leaflet.TOC.NestedLayer';
import MapSymbology from './../src/Leaflet.TOC.MapSymbology';
import fixtureGen from './fixtures/smalltree-custom';

import L from 'leaflet-headless';
import sinon from 'sinon';

describe('#generateID', () => {
  it('should return numbers', () => {
    expect(generateID()).to.be.a('number');
  })

  it('shouldn\'t generate the same ID twice', () => {
    const a = generateID(), b = generateID();
    expect(a).to.not.equal(b);
  })
})

describe('NestedLayer', () => {
  let l;
  let layerStub = sinon.createStubInstance(L.Layer);
  let mapStub = sinon.createStubInstance(L.Map);

  beforeEach(() => {
    l = new NestedLayer(666, 'Layer 666', layerStub, mapStub);
  })

  it('should have accessible properties', () => {
    expect(l).to.have.property('layerID');
    expect(l).to.have.property('name');
    expect(l).to.have.property('enabled');
    expect(l).to.have.property('selected');
    expect(l).to.have.property('layer');
    expect(l).to.have.property('children');
    expect(l).to.have.property('parent');
    expect(l).to.have.property('symbology');
    expect(l).to.have.property('minZoom');
    expect(l).to.have.property('maxZoom');
    expect(l).to.have.property('rules');
    expect(l).to.have.property('id');
  })

  describe('#constructor', () => {
    it('should throw errors when missing required arguments', () => {
      const layerID = 999;
      const name = 'Layer 999';
      expect(function() {
        new NestedLayer(undefined, name, layerStub, mapStub);
      }).to.throw('layer ID');

      expect(function() {
        new NestedLayer(layerID, undefined, layerStub, mapStub);
      }).to.throw('name');

      expect(function() {
        new NestedLayer(layerID, name, undefined, mapStub);
      }).to.throw('layer object');

      expect(function() {
        new NestedLayer(layerID, name, layerStub, undefined);
      }).to.throw('map object');
    })

    it('should have defaults for optional arguments', () => {
      expect(l.parent).to.equal(l.defaults.parent);
      // .equal tests referential equality (===)
      // .eql tests deep equality
      // http://chaijs.com/api/bdd/#method_eql
      expect(l.children).to.eql(l.defaults.children);
      expect(l.symbology).to.be.an.instanceof(MapSymbology);
      expect(l.enabled).to.equal(l.defaults.enabled);
      expect(l.selected).to.equal(l.defaults.selected);
      expect(l.zoom).to.equal(l.defaults.zoom);
    })
  })

  describe('#enabled', () => {
    before(() => {
      l = new NestedLayer(666, 'Layer 666', layerStub, mapStub, {
        rules: {
          enableTriggersAttach: true,
        }
      });
    })

    beforeEach(() => {
      sinon.spy(l, '_attach');
      sinon.spy(l, '_detach');
    })

    afterEach(() => {
      l._attach.restore();
      l._detach.restore();
    })

    it('should detach when disabled', () => {
      l.disable();
      expect(l.disabled).to.be.true;
      expect(l._detach).to.have.been.called;
    })

    it('should attach when enabled and selected and enableTriggersAttach is true', () => {
      l.select();
      l.enable();
      expect(l.enabled).to.be.true;
      expect(l.selected).to.be.true;
      expect(l.rules.enableTriggersAttach).to.be.true;
      expect(l._attach).to.have.been.called;
    })
  })

  describe('#selected', () => {
    it('should be deselected when selected but disabled and alwaysDeselectedWhenDisabled is true', () => {
      l = new NestedLayer(666, 'Layer 666', layerStub, mapStub, {
        rules: {
          alwaysDeselectedWhenDisabled: true
        }
      });

      l.select();
      l.disable();
      expect(l.disabled).to.be.true;
      expect(l.rules.alwaysDeselectedWhenDisabled).to.be.true;
      expect(l.deselected).to.be.true;
    })

    it('should always return the selected state when alwaysDeselectedWhenDisabled is false', () => {
      l = new NestedLayer(666, 'Layer 666', layerStub, mapStub, {
        rules: {
          alwaysDeselectedWhenDisabled: false
        }
      });

      l.select();
      l.disable();
      expect(l.selected).to.be.true;
      l.enable();
      expect(l.selected).to.be.true;

      l.deselect();
      l.disable();
      expect(l.deselected).to.be.true;
      l.enable();
      expect(l.deselected).to.be.true;
    })

    it('should disable children when deselected and disableDescendentsWhenDeselected is true', () => {
      const options = {
        rules: {
          disableDescendentsWhenDeselected: true
        }
      }

      let fixtures = fixtureGen(options);
      let fixture = fixtures.parent;
      fixture.deselect();

      expect(fixture.rules.disableDescendentsWhenDeselected).to.be.true;
      expect(fixture.deselected).to.be.true;
      fixture.children.forEach((child) => {
        expect(child.disabled).to.be.true;
      });

    })
  })

  describe('#children', () => {
    let child1, child2;

    before(() => {
      child1 = new NestedLayer(777, 'Layer 777', layerStub, mapStub);
      child2 = new NestedLayer(888, 'Layer 888', layerStub, mapStub);
    })

    it('should be able to add children', () => {
      l.addChild(child1);
      l.addChild(child2);
      expect(l.children).to.contain(child1);
      expect(l.children).to.contain(child2);
    })

    it('should make itself the parent of children it adds', () => {
      l.addChild(child1);
      child1.addChild(child2);
      expect(child1.parent).to.equal(l);
      expect(child2.parent).to.equal(child1);
    })

    it('should only be able to add NestedLayers as children', () => {
      const badChild = {};
      expect(() => {
        l.addChild(badChild);
      }).to.throw('NestedLayer');
    })

    it('#hasChildren', () => {
      expect(l.hasChildren).to.be.false;

      l.addChild(child1);
      expect(l.hasChildren).to.be.true;
    })
  })

})

/*global describe, expect, it, beforeEach*/
import * as NestedLayerTreeHelper from '../src/Leaflet.TOC.NestedLayerTreeHelper';
import NestedLayer from '../src/Leaflet.TOC.NestedLayer';
// import tree, * as fixtures from './fixtures/smalltree';

import L from 'leaflet-headless';
import sinon from 'sinon';

describe('NestedLayerTreeHelper', () => {
  let layer;
  let root1, root2, root3;
  let child1, child2; // children of root 2
  let tree;
  let layerStub = sinon.createStubInstance(L.Layer);
  let mapStub = sinon.createStubInstance(L.Map);
  const options = { rules: { alwaysDeselectedWhenDisabled: false } };

  beforeEach('create tree fixture', () => {
    // layer is an alias for root1
    layer = root1;
    root1 =   new NestedLayer(111, 'Layer 111', layerStub, mapStub, options);
    root2 =   new NestedLayer(222, 'Layer 222', layerStub, mapStub, options);
    root3 =   new NestedLayer(333, 'Layer 333', layerStub, mapStub, options);
    child1 =  new NestedLayer(444, 'Layer 444', layerStub, mapStub, options);
    child2 =  new NestedLayer(555, 'Layer 555', layerStub, mapStub, options);
    root3.addChild(child1);
    root3.addChild(child2);
    tree = [root1, root2, root3];
  })

  describe('#getLayerByID', () => {
    it('should find layers by ID', () => {
      const target = child2;
      const id = target.id;

      expect(NestedLayerTreeHelper.getLayerByID(tree, id)).to.equal(target);
    })

    it('should return null if the layer is not found', () => {
      const id = 12345;

      expect(NestedLayerTreeHelper.getLayerByID(tree, id)).to.be.null;
    })
  })

  describe('#getLayerByLayerID', () => {
    it('should find layers by server ID', () => {
      const target = child2;
      const id = target.layerID;

      expect(NestedLayerTreeHelper.getLayerByLayerID(tree, id)).to.equal(target);
    })

    it('should return null if the layer is not found', () => {
      const id = 12345;

      expect(NestedLayerTreeHelper.getLayerByLayerID(tree, id)).to.be.null;
    })
  })

  describe('#applyStateChangeToLayer', () => {
    it('should apply valid state changes', () => {
      const change = {
        selected: !layer.defaults.selected,
        enabled: !layer.defaults.enabled
      };
      NestedLayerTreeHelper.applyStateChangeToLayer(layer, change);

      expect(layer.selected).to.equal(change.selected);
      expect(layer.enabled).to.equal(change.enabled);
    })
  })

  describe('#applyStateChangeToTree', () => {
    it('should apply valid state changes', () => {
      const change = {
        selected: !layer.defaults.selected,
        enabled: !layer.defaults.enabled
      };
      NestedLayerTreeHelper.applyStateChangeToTree(tree, change);

      expect(root1.selected).to.equal(change.selected);
      expect(root2.selected).to.equal(change.selected);
      expect(root3.selected).to.equal(change.selected);
      expect(child1.selected).to.equal(change.selected);
      expect(child2.selected).to.equal(change.selected);
      expect(root1.enabled).to.equal(change.enabled);
      expect(root2.enabled).to.equal(change.enabled);
      expect(root3.enabled).to.equal(change.enabled);
      expect(child1.enabled).to.equal(change.enabled);
      expect(child2.enabled).to.equal(change.enabled);
    })
  })
})

/*global describe, expect, it, beforeEach*/
import React from 'react';
import { mount, render, shallow } from 'enzyme';

import tree, * as fixtures from '../fixtures/smalltree';
import LeafletTOC from '../../src/components/LeafletTOC';
import NestedLayer from '../../src/components/NestedLayer';

describe('<LeafletTOC />', () => {

  let wrapper;
  const title = 'Example Table of Contents Control';

  before(() => {
    wrapper = shallow(<LeafletTOC layers={tree} title={title} />);
  })

  it('should display a title', () => {
    expect(wrapper).to.have.exactly(1).descendants('h2');
    expect(wrapper.find('h2')).to.contain.text(title);
  })

  it('should have a branch with class .branch', () => {
    expect(wrapper.find('ul')).to.have.className('branch');
  })

  it('should have a NestedLayer component for each root-level layer', () => {
    expect(wrapper).to.have.exactly(tree.length).descendants(NestedLayer);
  })

})

