import React from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import LayerHierarchy from './Leaflet.LayerHierarchy';
import NestedLayer from './Leaflet.NestedLayer';
import classnames from 'classnames';

/**
 * React component to represent a LayerHierarchy
 * @param {object} props The component's props.
 * @param {LayerHierarchy} props.hierarchy The LayerHierarchy the component displays.
 */
export class NestedLayersComponent extends React.Component {
  /**
   * @param {object} props The component's props.
   * @param {LayerHierarchy} props.hierarchy The LayerHierarchy the component displays.
   */
  constructor(props) {
    super(props);
    this.state = {
      hierarchy: this.props.hierarchy
    }
  }

  /**
   * Describes the React props object
   * @type {Object}
   */
  static propTypes = {
    hierarchy: PropTypes.instanceOf(LayerHierarchy).isRequired
  };

  /**
   * Generates a unique key for rendering a layer in JSX
   * @param  {NestedLayer} layer The layer to use data from
   * @return {string}       A string unique to this layer
   */
  makeLayerKey(layer) {
    return (layer.id.toString + layer.name);
  }

  /**
   * Event handler to toggle a layer's selected state
   * @param  {NestedLayer} layer The layer to toggle
   */
  handleToggleSelected = (layer) => {
    const id = layer.id;
    const newHierarchy = this.state.hierarchy;
    newHierarchy.getLayerByID(id).toggleSelected();
    this.setState({
      hierarchy: newHierarchy
    });
  }

  /**
   * Recursively renders a NestedLayer using NestedLayerComponent. If the NestedLayer has
   * children, use this function as it not only renders a single NestedLayer, but also the subtree
   * of layers under the NestedLayer recursively.
   * @param  {NestedLayer} layer The layer to represent with a React component
   * @return {NestedLayerComponent}       A react component representing the given NestedLayer.
   */
  makeComponentFromLayer = (layer) => {
    // recursive function
    // 'leaf' is the base case
    // property initializer syntax + arrow function keeps the scope of 'this' through recursive calls
    let componentChildren;

    // branch: this layer has children
    if (layer.children.length > 0) {
      // leaves = layer.children.map(this.makeComponentFromLayer)
      componentChildren = (
        <ul className="branch">
          {layer.children.map(this.makeComponentFromLayer)}
        </ul>
      );
    }

    return (
      <NestedLayerComponent layer={layer} onToggleSelected={this.handleToggleSelected} key={this.makeLayerKey(layer)}>
        {componentChildren}
      </NestedLayerComponent>
    );

  }

  /**
   * Renders the component
   * @return {Component} JSX
   */
  render() {
    const roots = this.state.hierarchy.rootLayers;
    let components = [];

    for (let i = 0; i < roots.length; i++) {
      components.push(this.makeComponentFromLayer(roots[i]));
    }

    return (
      <div className="nested-layer-control-container">
        <h2>TOC CONTROL</h2>
        <ul className="branch nested-layer-control">
          {components}
        </ul>
      </div>
    );
  }
}

/**
 * React Component to represent a single NestedLayer
 * @param {object} props Component properties
 * @param {NestedLayer} props.layer The layer to represent
 * @param {function} props.onToggleSelected The callback to change the layer's selected state
 */
export class NestedLayerComponent extends React.Component {
  /**
   * @param {object} props Component properties
   * @param {NestedLayer} props.layer The layer to represent
   * @param {function} props.onToggleSelected The callback to change the layer's selected state
   */
  constructor(props) {
    super(props);
    this.state = {};
  }

  /**
   * Describes the React props object
   * @type {Object}
   */
  static propTypes = {
    layer: PropTypes.instanceOf(NestedLayer).isRequired,
    onToggleSelected: PropTypes.func.isRequired,
    children: PropTypes.any
  };

  /**
   * Calls the toggle selected state handler passed by the parent to change the selected state of
   * the layer
   */
  toggleSelected = () => {
    // updates both the component state and the LayerHierarchy structure
    this.props.onToggleSelected(this.props.layer);
  }

  /**
   * Returns a data URL suitable for use in a the src of an <img /> that contains the base 64
   * encoded layer swatch
   * @return {string} data URL containing the swatch
   */
  getSwatch = () => {
    return 'data:image/png;base64,' + this.props.layer.swatch;
  }

  /**
   * Renders the component
   * @return {Component} JSX
   */
  render() {
    const itemClassNames = classnames({
      leaf: true,
      enabled: this.props.layer.enabled,
      disabled: this.props.layer.disabled,
      selected: this.props.layer.selected,
      deselected: this.props.layer.deselected
    });
    return (
      <li className={itemClassNames} >
        <input type="checkbox" checked={this.props.layer.selected} onChange={this.toggleSelected}/>
        {this.props.layer.swatch.length > 0 &&
          <img src={this.getSwatch()} className="swatch" />
        }
        <span className="layer-name">{this.props.layer.name}</span>

        {this.props.children}
      </li>
    );
  }
}

/**
 * Leaflet plugin to display a hierarchial version of {@link http://leafletjs.com/reference-1.1.0.html#control-layers L.Control.Layers}. Uses React to wrap the
 * NestedLayersComponent.
 * @param  {LayerHierarchy} hierarchy The hierarchy to display in the control
 * @param  {Element} element   The DOM element to bind the control to
 * @param  {object} options   Configuration options that dictate how the control should behave
 */
export default class NestedLayers {

  /**
   * Initialize the plugin and {@link #NestedLayers#attach attaches} to the DOM.
   * @param  {LayerHierarchy} hierarchy The hierarchy to display in the control
   * @param  {Element} element   The DOM element to bind the control to
   * @param  {object} options   Configuration options that dictate how the control should behave
   * @param  {boolean} [options.followAncestorVisibility=true] Deselecting any ancestor makes its
   * children invisible (without changing their selected state)
   * @param {boolean} [options.propogateDeselectToChildren=false] Deselecting a parent also deselects
   * children (by changing their state)
   * @param {boolean} [options.followAncestorMutability=true] Deselecting any ancestor disables its
   * children (cannot change children's selected state)
   */
  constructor(hierarchy, element, options) {

    if (typeof hierarchy == 'undefined') {
      throw new Error('Missing hierarchy when creating NestedLayers control');
    }
    this.hierarchy = hierarchy;
    if (typeof element == 'undefined') {
      throw new Error('Missing DOM element when creating NestedLayers control');
    }
    this.element = element;

    // default options
    this._options = {
      followAncestorVisibility: true,
      propogateDeselectToChildren: false,
      followAncestorMutability: true
    };

    // overwrite defaults with passed options
    Object.assign(this._options, options);

    this._component = <NestedLayersComponent hierarchy={this.hierarchy} />

    this._isAttached = false;

    // bind to the DOM
    this.attach();
  }

  /**
   * The underlying hierarchy of layers the plugin visually represents
   * @type {LayerHierarchy}
   */
  get hierarchy() {
    return this._hierarchy;
  }
  /**
   * Shorthand accessor for the {@link #NestedLayers#hierarchy hierarchy} property
   */
  get h() {
    return this.hierarchy;
  }
  set hierarchy(val) {
    this._hierarchy = val;
  }

  /**
   * The DOM element that the control attaches to
   * @type {Element}
   */
  get element() {
    return this._element;
  }
  /**
   * Shorthand accessor for the {@link #NestedLayers#element element} property
   */
  get el() {
    return this.element;
  }
  set element(val) {
    this._element = val;
  }

  /**
   * The React component under the hood
   * @type {NestedLayersComponent}
   */
  get component() {
    return this._component;
  }

  /**
   * True if the plugin has attached itself to the DOM element
   * @type {boolean}
   */
  get isAttached() {
    return this._isAttached;
  }
  // no direct setting of 'isAttached' from outside the class
  // the attach() and detach() methods handle this state

  /**
   * Configuration options
   * @type {object}
   */
  get options() {
    return this._options;
  }

  /**
   * Bind to the DOM. Internally, it checks to see if it has already been attached, and if not,
   * uses React to render the {@link #NestedLayers#component component} to the specified {@link
   * #NestedLayers#element element}.
   */
  attach() {
    if (!this.isAttached) {
      ReactDOM.render(
        this.component,
        this.element
      );
    }
    this._isAttached = true;
  }

  /**
   * Unbind from the DOM. Releases the {@link #NestedLayers#element element} for reuse.
   */
  detach() {
    if (this.isAttached) {
      ReactDOM.unmountComponentAtNode(this.element);
    }
    this._isAttached = true;
  }

}

import { defaults } from 'lodash';
import React from 'react';
import ReactDOM from 'react-dom';

import LeafletTOC from './components/LeafletTOC';

export default class Control {

  static _defaults = {
    title: 'Layers'
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  constructor(layers, element, options) {
    // save arguments
    this._layers = layers;
    this._element = element;

    // set initial state
    this._options = {};
    defaults(this._options, options, this.defaults);
    this._component = <LeafletTOC layers={this.layers} title={this.options.title} />

    this.attach();
  }

  // all properties are get-only from the outside
  get layers() {
    return this._layers;
  }
  get element() {
    return this._element;
  }
  get options() {
    return this._options;
  }
  get component() {
    return this._component;
  }
  get isAttached() {
    return this._isAttached;
  }

  // bind to the DOM
  attach() {
    if (!this.isAttached) {
      ReactDOM.render(
        this.component,
        this.element
      );
    }
    this._isAttached = true;
  }

  detach() {
    if (this.isAttached) {
      ReactDOM.unmountComponentAtNode(this.element);
    }
    this._isAttached = false;
  }

}

import { defaultsDeep as defaults } from 'lodash';

let lastID = 0;

/**
 * Generate a ID number guaranteed to be unique.
 * IDs are composed of a pseudorandom 4-digit number suffixed by an incrementing counter.
 * @return {Number} Unique ID number
 */
export function generateID() {
  // generates a 4 digit random number
  // lastID is appended to the end of the number
  // lastID is incremented each
  return Number.parseInt(Math.floor(1000 + Math.random() * 9000).toString()
                         + (lastID++).toString());
}

/**
 * Wraps a Leaflet Layer to allow that layer to be the "parent" of other layers by having
 * "child" layers
 * @see http://leafletjs.com/reference-1.1.0.html#layer
 */
export default class NestedLayer {

  static _defaults = {
    parent: null,
    children: [],
    enabled: true,
    selected: false,
    minZoom: Number.NEGATIVE_INFINITY,
    maxZoom: Number.POSITIVE_INFINITY,
    rules: {
      enableTriggersAttach: true,
      alwaysDeselectedWhenDisabled: true
    },
    _isAttached: false
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  /**
   *
   * @param {object} props The layer data
   * @param {number} props.id The layer's unique ID number
   * @param {string} props.name The layer's unique name
   * @param {L.Layer} props.layer A {@link
   * http://leafletjs.com/reference-1.1.0.html#layer Leaflet layer}
   * @param {L.Map} props.map {@link
   * http://leafletjs.com/reference-1.1.0.html#map Leaflet map} to attach to
   * @param {boolean} [props.enabled=true] Initial enabled state of the layer
   * @param {boolean} [props.selected=false] Initial selected state of the layer
   * @param {string} [props.swatch=''] Base64 encoded swatch PNG
   * @param {NestedLayer[]} [props.children=[]] Child layers
   * @param {number} [props.minZoom] Minimum zoom level that the layer should be visible
   * @param {number} [props.maxZoom] Maximum zoom level that the layer should be visible
   */

  /**
   * Creates a new NestedLayer.
   * @param  {number} layerID ID of the layer on the origin.
   * @param  {string} name    Name of the layer on the origin; display-friendly
   * @param  {L.Layer} layer   Leaflet layer
   * @param  {L.Map} map     Leaflet map
   * @param  {object} props   Optional properties
   */
  constructor(layerID, name, layer, map, props) {

    // ensure all required props are present
    if (typeof layerID == 'undefined') {
      throw new Error('Missing layer ID when creating NestedLayer');
    }
    if (typeof name == 'undefined') {
      throw new Error('Missing name when creating NestedLayer');
    }
    if (typeof layer == 'undefined') {
      throw new Error('Missing layer object when creating NestedLayer');
    }
    if (typeof map == 'undefined') {
      throw new Error('Missing map object when creating NestedLayer');
    }

    // set the required props
    this._props = {
      layerID,
      name,
      layer,
      map
    };

    // merge optional props with defaults
    // the "defaults" also contains the initial state
    defaults(this._props, props, this.defaults);
    this._props.id = generateID();

    // if this layer is starting off selected, attach to the map
    // calling this.select() ensures that we follow any other attachment rules
    // built into the 'selected' setter
    if (this.selected) {
      this.select();
    }

    // if the zoom properties are on the leaflet layer object, copy them up to the
    // NestedLayer object
    if (this.layer.minZoom !== undefined && this.layer.maxZoom !== undefined) {
      this._props.minZoom = this.layer.minZoom;
      this._props.maxZoom = this.layer.maxZoom;
    }

    // if this layer has zoom data, we need to handle the case where the user zooms to a level where
    // our layer should be disabled according to the minZoom/maxZoom contained in the layer object
    // if (this.minZoom !== undefined && this.maxZoom !== undefined) {
    //   this.map.on('zoomend', this._handleMapZoom);
    // }
  }

  /**
   * Layer ID as specified by the server
   * @type {number}
   */
  get layerID() {
    return this._props.layerID;
  }

  /**
   * Layer name
   * @type {string}
   */
  get name() {
    return this._props.name;
  }

  /**
   * Underlying Leaflet layer
   * @type {L.Layer}
   */
  get layer() {
    return this._props.layer;
  }

  /**
   * Leaflet Map to attach to
   * @type {L.Map}
   */
  get map() {
    return this._props.map;
  }

  /**
   * Child layers
   * @type {NestedLayer[]}
   */
  get children() {
    return this._props.children;
  }

  /**
   * Parent layer
   * @type {NestedLayer}
   */
  get parent() {
    return this._props.parent;
  }

  /**
   * Minimum zoom level for this layer to be visible
   * @type {number}
   */
  get minZoom() {
    return this._props.minZoom;
  }

  /**
   * Maximum zoom level for this layer to be visible
   * @type {number}
   */
  get maxZoom() {
    return this._props.maxZoom;
  }

  get rules() {
    return this._props.rules;
  }

  /**
   * Unique ID generated for each NestedLayer
   * @type {number}
   */
  get id() {
    return this._props.id;
  }

  // enabled = user may freely toggle this layer on and off
  // disabled = user may not toggle the layer
  // disabling always deselects the layer, but the selected state is persisted, so that if
  // the layer is re-enabled, the selected state is what it was prior to disabling
  // i.e. if disabled, always deselected
  // this logic is handled in the .selected getter

  /**
   * Whether the user may freely toggle this layer on and off
   * @type {boolean}
   */
  get enabled() {
    return this._props.enabled;
  }
  set enabled(val) {
    this._props.enabled = val;

    // if disabling, detach from map
    if (!val) {
      this._detach();

    // if enabling, and marked selected (i.e. "re-enabling"), attach to map
    } else if (this.rules.enableTriggersAttach && this._props.selected) {
      this._attach();
    }
  }
  /**
   * The inverse of #enabled
   * @type {boolean}
   */
  get disabled() {
    return !this.enabled;
  }

  /** Enable the layer */
  enable() {
    this.enabled = true;
  }
  /** Disable the layer */
  disable() {
    this.enabled = false;
  }
  /** Toggle the layer's enabled state */
  toggleEnabled() {
    this.enabled = !this.enabled;
  }

  // selected = layer present on the map
  // deselected = layer not present on the map
  get selected() {
    if (this.rules.alwaysDeselectedWhenDisabled && this.disabled) {
      return false;
    } else {
      return this._props.selected;
    }
  }
  set selected(val) {
    this._props.selected = val;

    // attach/detach from map when needed
    // disable children from selection when unselected
    if (this.selected) {
      this._attach();
      // this.enableChildren();
    } else {
      this._detach();
      // this.disableChildren();
    }
  }
  get deselected() {
    return !this.selected;
  }
  select() {
    this.selected = true;
  }
  deselect() {
    this.selected = false;
  }
  toggleSelected() {
    this.selected = !this.selected;
  }

  // true if the layer has children
  get hasChildren() {
    return this.children.length > 0;
  }

  // add a child NestedLayer object
  addChild(child) {
    if (!(child instanceof NestedLayer)) {
      throw new TypeError('child is not a NestedLayer');
    }
    this._props.children.push(child);
  }

  // enableChildren() {
  //   this._applyStateChangeToAllChildren('enabled', true, this.children);
  // }

  // disableChildren() {
  //   this._applyStateChangeToAllChildren('enabled', false, this.children);
  // }

  // ownChildren() {
  //   this._applyStateChangeToAllChildren('owner', this.owner, this.children);
  // }

  // _applyStateChangeToAllChildren(prop, val, children) {
  //   // utility to recursively loop through children (and their children, etc.)
  //   // to change their state
  //   // IDEA: convert to a "deep map" function
  //   for (let i = 0; i < children.length; i++) {
  //     // make the state change
  //     children[i][prop] = val;

  //     // loop through children/subtrees when necessary
  //     if (children[i].hasChildren) {
  //       this._applyStateChangeToAllChildren(prop, val, children[i].children);
  //     }
  //   }
  // }

  // _handleMapZoom = () => {
  //   const zoom = this.map.getZoom();

  //   if (zoom < this.minZoom || zoom > this.maxZoom) {
  //     this._detach();
  //   } else {
  //     this._attach()
  //   }
  // }

  // display on map
  _attach() {
    if (!this._isAttached) {
      this.map.addLayer(this.layer);
      // this.layer.addTo(this.map);
      this._isAttached = true;
    }
  }

  // remove from map
  _detach() {
    if (this._isAttached) {
      this.map.removeLayer(this.layer);
      // this.layer.removeFrom(this.map);
      this._isAttached = false;
    }
  }
}

