import { defaults } from 'lodash';
import React from 'react';
import ReactDOM from 'react-dom';

import LeafletTOC from './components/LeafletTOC';

export default class Control {

  static _defaults = {
    title: 'Layers'
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  constructor(layers, element, options) {
    // save arguments
    this._layers = layers;
    this._element = element;

    // set initial state
    this._options = {};
    defaults(this._options, options, this.defaults);
    this._component = <LeafletTOC layers={this.layers} title={this.options.title} />

    this.attach();
  }

  // all properties are get-only from the outside
  get layers() {
    return this._layers;
  }
  get element() {
    return this._element;
  }
  get options() {
    return this._options;
  }
  get component() {
    return this._component;
  }
  get isAttached() {
    return this._isAttached;
  }

  // bind to the DOM
  attach() {
    if (!this.isAttached) {
      ReactDOM.render(
        this.component,
        this.element
      );
    }
    this._isAttached = true;
  }

  detach() {
    if (this.isAttached) {
      ReactDOM.unmountComponentAtNode(this.element);
    }
    this._isAttached = false;
  }

}

/**
 * Parses the layer data served by an ArcGIS MapServer
 * @param {string} url A valid and reachable ArcGIS MapServer URL
 * @param {L.Map} map A Leaflet Map object, which will be passed to all
 * the layers in the resulting LayerHierarchy
 * @param {object} [options] An optional configuration object
 */
export default class MapServerParser {

  static APIEndpoints = {
    layers: '/layers',
    legend: '/legend'
  };

  static QueryParameters = {
    f: 'json'
  };

  static Headers = {
    Accept: 'application/json'
  };

  /**
   * Initializes the parser with the given parameters
   * @param {string} url     A valid and reachable ArcGIS MapServer URL
   * @param {L.Map} map     A Leaflet Map object, which will be passed to all
   * the layers in the resulting LayerHierarchy
   * @param {object} [options] An optional configuration object
   * @param {object} [options.data] The configuration options related to what data
   * should be parsed from the MapServer to be included on NestedLayer objects
   * @param {boolean} [options.data.scale=true] Include minZoom and maxZoom
   * parameters on the NestedLayer
   * @param {boolean} [options.data.defaultVisibility=true] Include the
   * initial selected state of the NestedLayer
   * @param {object} [options.hierarchyOptions={}] The configuration options
   * the NestedHierarchy takes on instantiation
   */
  constructor(url, map, options) {
    // ensure required parameters are present
    if (typeof url == 'undefined') {
      throw new Error('Missing URL when creating MapServerParser');
    }
    if (typeof map == 'undefined') {
      throw new Error('Missing Leaflet map object when creating MapServerParser');
    }

    // save url
    this._url = url;

    // save map
    this._map = map;

    // set default options
    this._defaults = {
      data: {
        scale: true,
        defaultVisibility: true,
        swatch: false
      },
      hierarchyOptions: {}
    }

    // merge options with defaults
    this._options = {};
    // https://lodash.com/docs/4.17.4#defaultsDeep
    defaults(this._options, options, this.defaults);
  }

  /**
   * MapServer URL
   * @type {string}
   */
  get url() {
    return this._url;
  }

  /**
   * Leaflet Map object
   * @type {L.Map}
   */
  get map() {
    return this._map;
  }

  /**
   * Configuration options
   * @type {object}
   */
  get options() {
    return this._options;
  }

  /**
   * Default configuration options
   * @type {object}
   */
  get defaults() {
    return this._defaults;
  }

  parse() {
    // we have to wait for both xhr promises to resolve before we can begin processing
    // note that Promise.join returns a promise that resolves to a LayerHierarchy
    return Promise.join(this._queryLayers(), this._queryLegend(), (layerRes, legendRes) => {
      // ensure we hit HTTP 2xx statuses
      if (!layerRes.ok || !legendRes.ok) {
        throw new Error('Bad request: HTTP status was not 2xx');
      }

      // parse the layerdata into JSON
      let body = layerRes.body;
      // ArcGIS does not properly set its Content-Type header
      // so force JSON parsing if superagent did not parse automatically
      if (layerRes.type !== MapServerParser.Headers.Accept) {
        body = JSON.parse(layerRes.text);
      }

      // parse the legend into JSON
      let legend = legendRes.body;
      // ArcGIS does not properly set its Content-Type header
      // so force JSON parsing if superagent did not parse automatically
      if (legendRes.type !== MapServerParser.Headers.Accept) {
        legend = JSON.parse(legendRes.text);
      }
      // the actual legend is located under the 'layers' node
      legend = legend.layers;

      // create the LayerHierarchy
      var hierarchy = new LayerHierarchy(this.options.hierarchyOptions);

      // move layers down as children of other layers
      body.layers.forEach((node) => {
        let layer = this._convertLayerNodeToNestedLayer(node, legend);
        let parent = (node.parentLayer !== null ? node.parentLayer.id : undefined);
        // if this layer has no parent, addLayer(...) will add as a root layer
        hierarchy.addLayer(layer, parent);
      });

      // resolve the promise with the resulting LayerHierarchy
      return hierarchy;
    });
  }

  /**
   * Queries the layers endpoint of the MapServer for layer data.
   * @return {Promise} Promise that resolves the request.
   */
  _queryLayers() {
    // assemble layerdata url
    const layerdataURL = this.url + MapServerParser.APIEndpoints.layers;

    // fetch layerdata as JSON
    return request
      .get(layerdataURL)
      .set(MapServerParser.Headers)
      .query(MapServerParser.QueryParameters)
      .end();
  }

  _queryLegend() {
    // assemble legend url
    const legendURL = this.url + MapServerParser.APIEndpoints.legend;

    // fetch legend as JSON
    return request
      .get(legendURL)
      .set(MapServerParser.Headers)
      .query(MapServerParser.QueryParameters)
      .end();
  }

  _convertLayerNodeToNestedLayer = (node, legend) => {
    let nestedLayerData = {
      id: node.id,
      name: node.name.trim(),
      map: this.map
    };
    // Leaflet layer data
    let leafletLayerData = {
      url: MapServerParser._makeLayerURL(this.url, nestedLayerData.id)
    };

    // get scale/zoom data from layer node
    if (this.options.data.scale) {
      // converts scale factor from ArcGIS to Leaflet's zoom factor
      // http://leafletjs.com/reference-1.1.0.html#crs-scale
      // leafletLayerData.maxZoom = this.map.options.crs.zoom(node.maxScale);
      // leafletLayerData.minZoom = this.map.options.crs.zoom(node.minScale);
      console.warn('Floor\'ing maxZoom and ceil\'ing minZoom');
      // console.log(nestedLayerData.name, 'minZoom', leafletLayerData.minZoom, 'maxZoom', leafletLayerData.maxZoom)
      leafletLayerData.maxZoom = Math.floor(this.map.options.crs.zoom(node.maxScale));
      leafletLayerData.minZoom = Math.ceil(this.map.options.crs.zoom(node.minScale));
    }

    // attach the swatch data
    // if (this.options.data.swatch && legend[nestedLayerData.id].legend.length > 0) {
    //   nestedLayerData.swatch = legend[nestedLayerData.id].legend[0].imageData;
    //   console.log(nestedLayerData.name, nestedLayerData.id, nestedLayerData.swatch);
    // }
    if (this.options.data.swatch) {
      nestedLayerData.swatch = MapServerParser._findSwatchInLegend(legend, nestedLayerData.id);
    }

    // attach the Leaflet layer object to the NestedLayer's data
    nestedLayerData.layer = new FeatureLayer(leafletLayerData);

    // create a new NestedLayer that wraps the Leaflet layer, map, etc.
    let layer = new NestedLayer(nestedLayerData);

    // set the selected state = to the node's default visibility state
    if (this.options.data.defaultVisibility) {
      layer.selected = node.defaultVisibility;
    }

    // returns a NestedLayer object
    return layer;
  }

  static _findSwatchInLegend(legend, layerID) {
    // if no swatches even exist (e.g. invalid legend)
    if (legend.length <= 0) { return ''; }

    // loop through to try to find a valid swatch
    // this algorithm could be optimized from O(n) due to
    // the sorted nature of the legend
    // it just must account for the fact that a legend object may not exist for
    // any given layer
    for (let i = 0; i < legend.length; i++) {
      if (legend[i].layerId == layerID && legend[i].legend.length > 0) {
        return legend[i].legend[0].imageData;
      }
    }

    // no swatch found
    return '';
  }

  static _makeLayerURL(baseURL, layerID) {
    if (typeof baseURL !== 'string') {
      throw new Error('Base URL was undefined, null, or was not a string');
    }
    if (typeof layerID !== 'number') {
      throw new Error('ID was undefined, null, or was not a number');
    }

    return baseURL + '/' + layerID.toString();
  }

}

import { defaultsDeep as defaults } from 'lodash';
// superagent mimics the node 'request' library on the client side as an xhr library
import superagent from 'superagent';
import Promise from 'bluebird';
import superagentPromise from 'superagent-promise';
const request = superagentPromise(superagent, Promise);

import NestedDynamicMapLayer from './Leaflet.TOC.NestedDynamicMapLayer';
import NestedGroupLayer from './Leaflet.TOC.NestedGroupLayer';
import * as NestedLayerTreeHelper from './Leaflet.TOC.NestedLayerTreeHelper';
import MapSymbol from './Leaflet.TOC.MapSymbol';
import { DynamicMapLayer } from 'esri-leaflet';

export default class MapServerParser {

  static _defaults = {
    parsingOptions: {
      scale: true,
      defaultVisibility: true,
      symbology: false
    },
    layerProps: {},
    dynamicMapLayerOptions: {}
  };

  static APIEndpoints = {
    layers: '/layers',
    legend: '/legend'
  };

  static QueryParameters = {
    f: 'json'
  };

  static Headers = {
    Accept: 'application/json'
  };

  constructor(url, map, options) {
    // ensure required parameters are present
    if (typeof url == 'undefined') {
      throw new Error('Missing URL when creating MapServerParser');
    }
    if (typeof map == 'undefined') {
      throw new Error('Missing Leaflet map object when creating MapServerParser');
    }

    // save url
    this._url = url;

    // save map
    this._map = map;

    // https://lodash.com/docs/4.17.4#defaultsDeep
    this._options = defaults({}, options, this.defaults);

    // create a new DynamicMapLayer to use in all of our NestedDynamicMapLayers
    let layerOptions = defaults({ url }, this.options.dynamicMapLayerOptions)
    this._layer = new DynamicMapLayer(layerOptions);
  }

  /**
   * MapServer URL
   * @type {string}
   */
  get url() {
    return this._url;
  }

  /**
   * Leaflet Map object
   * @type {L.Map}
   */
  get map() {
    return this._map;
  }

  /**
   * Configuration options
   * @type {object}
   */
  get options() {
    return this._options;
  }

  /**
   * Default configuration options
   * @type {object}
   */
  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }

  /**
   * Esri-Leaflet DynamicMapLayer object
   * @type {DynamicMapLayer}
   */
  get layer() {
    return this._layer;
  }

  parse() {
    // we have to wait for both xhr promises to resolve before we can begin processing
    // note that Promise.join returns a promise that resolves to a LayerHierarchy
    return Promise.join(this._queryLayers(), this._queryLegend(), (layerResponse, legendResponse) => {
      // ensure we hit HTTP 2xx statuses
      if (!layerResponse.ok || !legendResponse.ok) {
        throw new Error('Bad request: HTTP status was not 2xx');
      }

      // parse the layer data into JSON
      let layers = this._parseBodyAsJSON(layerResponse);

      // parse the legend data into JSON
      let legend = this._parseBodyAsJSON(legendResponse);

      // restrict ourselves to the 'layers' node of the response bodies
      layers = layers.layers;
      legend = legend.layers;

      let tree = [];

      layers.forEach((node) => {
        let layer = this._parseNode(node);

        if (this.options.parsingOptions.defaultVisibility) {
          layer.selected = node.defaultVisibility;
        }

        if (this.options.parsingOptions.symbology) {

          // find the entry in the legend corresponding to the current layer
          let legendEntry = legend.find((legendNode) => {
            return legendNode.layerId == layer.layerID;
          })

          // confirm that there is symbology for the layer
          if (legendEntry && legendEntry.legend && legendEntry.legend.length > 0) {

            // add each entry from the legend node in the entry as a MapSymbol
            legendEntry.legend.forEach((entry, idx) => {
              layer.symbology.addSymbol(new MapSymbol(entry));
            })

          }
        }

        // add the layer into the proper subtree
        if (node.parentLayer !== null) {
          let parentID = node.parentLayer.id;
          // this assumes that the parent has already been parsed
          let parent = NestedLayerTreeHelper.getLayerByLayerID(tree, parentID);
          parent.addChild(layer);
        } else {
          tree.push(layer);
        }
      })

      return tree;
    });
  }

  _queryLayers() {
    // assemble layerdata url
    const layerdataURL = this.url + this.constructor.APIEndpoints.layers;

    // fetch layerdata as JSON
    return request
      .get(layerdataURL)
      .set(this.constructor.Headers)
      .query(this.constructor.QueryParameters)
      .end();
  }

  _queryLegend() {
    // assemble legend url
    const legendURL = this.url + this.constructor.APIEndpoints.legend;

    // fetch legend as JSON
    return request
      .get(legendURL)
      .set(this.constructor.Headers)
      .query(this.constructor.QueryParameters)
      .end();
  }

  _parseBodyAsJSON(response) {
    // parse the data into JSON
    let body = response.body;
    // ArcGIS does not properly set its Content-Type header
    // so force JSON parsing if superagent did not parse automatically
    if (response.type !== this.constructor.Headers.Accept) {
      body = JSON.parse(response.text);
    }

    return body;
  }

  _parseNode(node) {
    let layerType;

    switch (node.type) {
      case 'Group Layer':
        layerType =  NestedGroupLayer;
        break;
      case 'Feature Layer':
        layerType = NestedDynamicMapLayer
        break;
      default:
        throw 'Invalid layer type: ' + node.type;
        break;
    }

    return new layerType(
      node.id,
      node.name,
      this.layer,
      this.map,
      this.options.layerProps);
  }

}

import { defaultsDeep as defaults } from 'lodash';

export default class MapSymbol {

  static _defaults = {
    label: '',
    height: 20,
    width: 20,
    contentType: 'image/png',
    imageData: '',
    relativeURL: ''
  }

  constructor(props) {
    // merge props with defaults
    this._props = {};
    // https://lodash.com/docs/4.17.4#defaultsDeep
    defaults(this._props, props, this.defaults);
  }

  get label() {
    return this._props.label;
  }

  get height() {
    return this._props.height;
  }

  get width() {
    return this._props.width;
  }

  get contentType() {
    return this._props.contentType;
  }

  get imageData() {
    return this._props.imageData;
  }

  get relativeURL() {
    return this._props.relativeURL;
  }

  get base64URI() {
    return 'data:' + this.contentType + ';base64,' + this.imageData;
  }

  get defaults() {
    // uses reflection to return the static _defaults property on the class
    return this.constructor._defaults;
  }
}

export default class MapSymbology {

  constructor(symbols = []) {
    this._symbols = symbols;
  }

  get symbols() {
    return this._symbols;
  }

  addSymbol(symbol) {
    this._symbols.push(symbol);
  }

}

import NestedLayer from './Leaflet.TOC.NestedLayer';

export default class NestedDynamicMapLayer extends NestedLayer {
  _attach() {
    // console.log(this.layer.getLayers);
    if (!this._isAttached) {
      // push the layerID into the list of layers to render
      // http://esri.github.io/esri-leaflet/api-reference/layers/dynamic-map-layer.html#methods

      let layers = this.layer.getLayers();
      // if the DynamicMapLayer was instantiated without providing a list of layer IDs as an option,
      // then .getLayers will return 'false' (as of esri-leaflet@2.0.8)
      // in JavaScript, 'typeof []'' will return 'object', so use Array.isArray to verify that .getLayers
      // returned an actual array
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/typeof
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/isArray
      if (!Array.isArray(layers)) {
        // .getLayers returned false (see above), so set the empty array ourselves
        layers = [];
      }
      // setLayers allows you to introduce duplicate IDs
      // this check prevents that from happening
      // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/includes
      if (!layers.includes(this.layerID)) {
        // Array.push returns the new length of the array, not the array itself
        // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/push
        layers.push(this.layerID);
        this.layer.setLayers(layers);
      }


      // ensure the Leaflet layer is actually on the map object
      if (!this.map.hasLayer(this.layer)) {
        this.map.addLayer(this.layer);
      }
    }

    this._isAttached = true;
  }

  _detach() {
    if (this._isAttached) {
      // remove the layerID from the list of layers to render
      // http://esri.github.io/esri-leaflet/api-reference/layers/dynamic-map-layer.html#methods
      let layers = this.layer.getLayers().filter((id) => {
        return id !== this.layerID;
      });
      this.layer.setLayers(layers);

      // if there are no more layers left, remove the layer from the map object
      if (layers.length === 0 && this.map.hasLayer(this.layer)) {
        this.map.removeLayer(this.layer);
      }
    }

    this._isAttached = false;
  }
}

